/*
Title: Overview of Pollutants Emissions
*/

SELECT year,
    AVG(co2) AS co2, 
    AVG(thc_emissions) AS thc, 
    AVG(co_emissions) AS co, 
    AVG(nox_emissions) AS nox, 
    AVG(thc_nox_emissions) AS thc_nox, 
    AVG(particulates_emissions) AS particulates
FROM car_fuel
GROUP BY year
ORDER BY year;

